  ///
  /// 
  /// MAIN METHODS
  /// 
  /// 
  static TextStyle getHeadingTextStyle(BuildContext context) => Theme.of(context).textTheme.headline1;

  static Text headingBold(BuildContext context, String text, {double textSize, TextStyle textStyle, Color textColor, TextAlign textAlign, int maxLines, TextOverflow textOverflow}) {
      //textColor ??= ColorUtils.darkTitleTextColor;


    return Text(text ?? '', textAlign: textAlign,
        overflow: textOverflow,
        maxLines: maxLines,
        style: textStyle ?? getHeadingTextStyle(context).copyWith(fontSize: textSize, fontWeight: FontWeight.w700, color: textColor));
  }

  static Text headingLight(BuildContext context, String text, {double textSize, TextStyle textStyle, Color textColor, TextAlign textAlign, int maxLines, TextOverflow textOverflow}) {
      textColor ??= ColorUtils.darkTitleTextColor;

      textAlign ??= TextAlign.left;

    return Text(text ?? '',
        textAlign: textAlign,
        overflow: textOverflow,
        maxLines: maxLines,
        style: textStyle ?? getHeadingTextStyle(context).copyWith(fontSize: textSize, fontWeight: FontWeight.w300, color: textColor));
  }

  static Text getTitle(BuildContext context, String text, {double textSize, TextStyle textStyle, Color textColor, TextAlign textAlign, TextOverflow textOverflow , int maxLines, FontWeight fontWeight = FontWeight.normal}) {
      textColor ??= ColorUtils.defaultTextColor;

      textAlign ??= TextAlign.left;

    return Text(text ?? '', textAlign: textAlign,
        overflow: textOverflow,
        maxLines: maxLines,
        style: textStyle ?? getTitleTextStyle(context).copyWith(fontSize: textSize, fontWeight: fontWeight, color: textColor));
  }

  static TextStyle getTitleTextStyle(BuildContext context) => Theme.of(context).textTheme.headline2;

  static TextStyle getHintTextStyleDefault(BuildContext context) => Theme.of(context).textTheme.headline2.copyWith(fontSize: 15.0, fontWeight: FontWeight.w400, color: ColorUtils.defaultTextColor);
}
